const s=document.querySelector(".search_icon"),e=document.querySelector("#search-section"),n=document.querySelector("#search-cancel");s.addEventListener("click",()=>{e.classList.contains("flex")?(e.classList.remove("flex"),e.classList.add("hidden")):(e.classList.remove("hidden"),e.classList.add("flex"))});n.addEventListener("click",()=>{e.classList.contains("flex")&&(e.classList.remove("flex"),e.classList.add("hidden"))});const t=document.querySelector(".close_icon"),i=document.querySelector(".bar-3_icon"),c=document.querySelector("#header-phone");t.addEventListener("click",()=>{c.classList.contains("block")&&(c.classList.remove("block"),c.classList.add("hidden"))});i.addEventListener("click",()=>{c.classList.contains("hidden")&&(c.classList.add("block"),c.classList.remove("hidden"))});
